prose.customCSS:
blockquote {
  border-left: 3px solid #58a66f;
  padding-left: 1.5rem;
  margin: 1.5rem 0;
  font-style: italic;
  color: #383838;
}

pre {
  background: #f5f5f5;
  padding: 1rem;
  border-radius: 6px;
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.9em;
  overflow-x: auto;
  white-space: pre-wrap !important;
  word-wrap: break-word !important;
  border: 1px solid #e4e4e4;
}

pre code {
  white-space: pre-wrap !important;
  word-wrap: break-word !important;
}

code:not(pre code) {
  background: #f5f5f5;
  padding: 0.15em 0.4em;
  border-radius: 3px;
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.9em;
}

customCSS:
.prose {
  color: var(--color-fg-muted);
}
.prose h1,
.prose h2,
.prose h3,
.prose h4 {
  color: var(--color-base-alt);
  font-family: 'ff-meta-serif-web-pro', serif;
}
.prose a {
  color: var(--color-brand);
  text-decoration: underline;
  text-underline-offset: 2px;
}
.prose a:hover {
  color: var(--color-base-alt);
}

/* Dark section descendant overrides */
.lta-section-dark .heading-xl,
.lta-section-dark .heading-lg,
.lta-section-dark .heading-md,
.lta-section-dark .heading-sm,
.lta-section-dark .heading-xs,
.lta-section-dark h1, .lta-section-dark h2,
.lta-section-dark h3, .lta-section-dark h4 {
  color: var(--color-panel);
}
.lta-section-dark a {
  color: var(--color-orange);
}

/* Grunge texture overlay */
.lta-grunge-bg {
  position: relative;
}
.lta-grunge-bg::before {
  content: '';
  position: absolute;
  inset: 0;
  z-index: 0;
  opacity: 0.12;
  background-image: url('/assets/textures/grunge@2x.png');
  background-size: 800px;
  background-position: center;
  background-repeat: repeat;
}

/* Topography texture header */
.lta-topography-header {
  position: relative;
}
.lta-topography-header::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 1.5rem;
  background-image: url('/assets/textures/topography.png');
  background-size: 400%;
  background-repeat: repeat-x;
  background-position: left top;
}

/* Expandable active state */
details[open] {
  border-color: var(--color-orange);
}
details[open] > summary {
  background: linear-gradient(to bottom, var(--color-base-muted), transparent);
}
details[open] > summary .lta-expand-title {
  color: var(--color-orange);
}

/* Stats color cycling */
.lta-stat-item {
  --lta-stat-color: var(--color-brand);
}
.lta-stat-item:nth-child(3n+2) {
  --lta-stat-color: var(--color-blue);
}
.lta-stat-item:nth-child(3n) {
  --lta-stat-color: var(--color-orange);
}
.lta-stat-item {
  color: var(--lta-stat-color);
}
.lta-stat-label,
.lta-stat-link {
  color: inherit;
}
