prose.customCSS:
blockquote {
  border-left: 3px solid currentColor;
  padding-left: 1.5rem;
  margin: 1.5rem 0;
  font-style: italic;
  opacity: 0.85;
}

pre {
  background: rgba(0, 0, 0, 0.3);
  padding: 1rem;
  border-radius: 4px;
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.9em;
  overflow-x: auto;
  white-space: pre-wrap !important;
  word-wrap: break-word !important;
}

pre code {
  white-space: pre-wrap !important;
  word-wrap: break-word !important;
}

code:not(pre code) {
  background: rgba(0, 0, 0, 0.2);
  padding: 0.15em 0.4em;
  border-radius: 3px;
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.9em;
}

theme.customCSS:
.prose {
  color: var(--fg-muted);
}
.prose h1,
.prose h2, 
.prose h3, 
.prose h4 {
  color: var(--fg);
}

partials.template:
{{#switch style}}
  {{#case "primary"}}
    <a href="{{link}}" class="btn btn-md btn-primary">
      {{default label "Click Here"}}
    </a>
  {{/case}}
  {{#case "secondary"}}
    <a href="{{link}}" class="btn btn-md btn-secondary">
      {{default label "Click Here"}}
    </a>
  {{/case}}
  {{#case "outline"}}
    <a href="{{link}}" class="btn btn-md btn-outline">
      {{default label "Click Here"}}
    </a>
  {{/case}}
  {{#case "ghost"}}
    <a href="{{link}}" class="btn btn-md btn-ghost">
      {{default label "Click Here"}}
    </a>
  {{/case}}
  {{#otherwise}}
    <a href="{{link}}" class="btn btn-md btn-primary">
      {{default label "Click Here"}}
    </a>
  {{/otherwise}}
{{/switch}}

{{#switch type}}
  {{#case "email"}}
  <div>
    <label for="{{name}}" class="block body-md text-fg-muted">{{default label "Email"}}</label>
    <div class="mt-2.5">
      <input id="{{name}}" type="email" name="{{name}}" autocomplete="email" class="block w-full rounded-md bg-white/5 px-3.5 py-2 text-base text-white outline outline-1 -outline-offset-1 outline-white/10 placeholder:text-gray-500 focus:outline focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-500">
    </div>
  </div>
  {{/case}}
  {{#case "textarea"}}
  <div>
    <label for="{{name}}" class="block body-md text-fg-muted">{{default label "Message"}}</label>
    <div class="mt-2.5">
      <textarea id="{{name}}" name="{{name}}" rows="4" class="block w-full rounded-md bg-white/5 px-3.5 py-2 text-base text-white outline outline-1 -outline-offset-1 outline-white/10 placeholder:text-gray-500 focus:outline focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-500"></textarea>
    </div>
  </div>
  {{/case}}
  {{#otherwise}}
  <div>
    <label for="{{name}}" class="block body-md text-fg-muted">{{default label "Text"}}</label>
    <div class="mt-2.5">
      <input id="{{name}}" type="{{name}}" name="{{name}}" autocomplete="text" class="block w-full rounded-md bg-white/5 px-3.5 py-2 text-base text-white outline outline-1 -outline-offset-1 outline-white/10 placeholder:text-gray-500 focus:outline focus:outline-2 focus:-outline-offset-2 focus:outline-indigo-500">
    </div>
  </div>
  {{/otherwise}}
{{/switch}}
